import styled from "styled-components";

export const Botones = styled.button `
      border:0px;
      background-color: rgb(198, 15, 15);
      width: fit-content;
      height: fit-content;
      border-radius: 15px;
      transition: 0.5s;    
      color: white;
      margin-bottom: 20px;
      text-align: right;
`;

export const DivCard = styled.div `
      background-color:blue;
      width: fit-content;
      padding: 1em;
      border-radius: 10px;
      box-shadow: 10px 10px 5px 0px rgba(0,0,0,0.75);
      text-shadow: 6px 4px 7px rgba(0,0,0,0.6);
      display: flex;
      flex-direction: column;
      margin-bottom: 10px;
      text-align: center;
`;  
export const divBoton = styled.div `
   display: flex;
   justify-content: flex-end;
   background-color: black;
`

function Card({name, species, gender, image, onClose}) {
   return (
      <DivCard>
         <divBoton><Botones onClick={onClose}>X</Botones></divBoton>
         <img  src={image} alt={''} />
         <h2>Nombre: {name}</h2>
         <h2>Especie: {species}</h2>
         <h2>GÃ©nero: {gender}</h2>
      </DivCard>
   );
}
export default Card;
