                        <Link to={`/detail/${id}`}> <h2> {name}</h2> </Link>
                        <h2>Especie: {species}</h2>
                        <h2>G√©nero: {gender}</h2>
                  </DivChar>
            </DivCard>
      );
}
const mapDispatchToProps = (dispatch) => {
      return {
            agregarPersonaje: () => {
                  dispatch(agregarPersonaje())
